import React, { useState } from 'react'
import "../components/CSS/style.css"
import { Scrollbars } from 'react-custom-scrollbars-2';
import Userdata from "./Userdata"
import Sidebar from "./Sidebar"
import AddNewDivision from "./AddNewDivision"


const Tasks = () => {

    const [set, setData] = useState([]);


    // useEffect(() => {
    //     const fetchApi = async () => {
    //         const url = "https://api.facthunt.in/fostergem/v1/post/list/public"
    //         const response = await fetch(url);

    //         const dataJson = await response.json();
    //         // console.log(dataJson.content);
    //         setData(dataJson.content);
    //     }

    //     fetchApi();



    // }, [setData])

    const handleDelete = () => {
        setData([])
    }

    return (

        <>
            <div className="container">
                {/*Sidebar*/}

                <Sidebar />

                {/*Right Section*/}

                <div className="right-section">

                    <div className="right-sec-head">
                        <p>Discover</p>
                        <div className="btn_class">
                            <button className="btn" onClick={handleDelete}>Clear Record</button>
                        </div>
                    </div>
                    <div className="heading-rgt2">
                        <i class="fa-solid fa-arrow-left-long"></i>
                        <h2>Century</h2>
                        <i class="fa-solid fa-greater-than"></i>
                        <h5 style={{ color: "#058970a3", fontSize: "1.5rem" }}>Divisions</h5>
                    </div>

                    <div className="right_parent">
                        <div className="parent_cards">
                            <Scrollbars>
                                <div className="contain">





                                    <Userdata />




                                </div>
                            </Scrollbars>


                        </div>
                        <div className="dynamic">
                            <div className="division_btn">
                                <button className="btn_divi">


                                    <i class="fa-solid fa-plus" style={{marginRight:".7rem"}}></i>Add New Division</button>
                            </div>
                        </div>
                    </div>


                </div>
     <AddNewDivision/>

            </div>
        </>
    )
}

export default Tasks
